(294, '_transient_storefront_extensions', 'O:8:"stdClass":1:{s:8:"products";a:13:{i:0;O:8:"stdClass":5:{s:5:"title";s:20:"Storefront Powerpack";s:5:"image";s:0:"";s:7:"excerpt";s:124:"With Storefront Powerpack, you can customize your store''s colors, fonts, text and more - all without ever touching any code.";s:4:"link";s:121:"https://woocommerce.com/products/storefront-powerpack/?utm_source=product&utm_medium=upsell&utm_campaign=storefrontaddons";s:5:"price";s:10:"&#36;59.00";}i:1;O:8:"stdClass":5:{s:5:"title";s:35:"Storefront Homepage Contact Section";s:5:"image";s:0:"";s:7:"excerpt";s:49:"Add a Contact section to the Storefront homepage.";s:4:"link";s:136:"https://woocommerce.com/products/storefront-homepage-contact-section/?utm_source=product&utm_medium=upsell&utm_campaign=storefrontaddons";s:5:"price";s:9:"&#36;0.00";}i:2;O:8:"stdClass":5:{s:5:"title";s:25:"Storefront Hamburger Menu";s:5:"image";s:0:"";s:7:"excerpt";s:120:"Storefront Hamburger Menu turns the default handheld navigation into an off-screen sidebar menu with a hamburger toggle.";s:4:"link";s:126:"https://woocommerce.com/products/storefront-hamburger-menu/?utm_source=product&utm_medium=upsell&utm_campaign=storefrontaddons";s:5:"price";s:9:"&#36;0.00";}i:3;O:8:"stdClass":5:{s:5:"title";s:29:"Storefront Product Pagination";s:5:"image";s:0:"";s:7:"excerpt";s:168:"Displays next and previous links on single product pages, with a thumbnail and title revealed on hover. Products are in chronological order and display is customizable.";s:4:"link";s:130:"https://woocommerce.com/products/storefront-product-pagination/?utm_source=product&utm_medium=upsell&utm_campaign=storefrontaddons";s:5:"price";s:9:"&#36;0.00";}i:4;O:8:"stdClass":5:{s:5:"title";s:26:"Storefront Product Sharing";s:5:"image";s:0:"";s:7:"excerpt";s:155:"Add an attractive sharing component to product pages, prompting customers and visitors to share your products on Facebook, Twitter, Pinterest or via email.";s:4:"link";s:127:"https://woocommerce.com/products/storefront-product-sharing/?utm_source=product&utm_medium=upsell&utm_campaign=storefrontaddons";s:5:"price";s:9:"&#36;0.00";}i:5;O:8:"stdClass":5:{s:5:"title";s:29:"Storefront Sticky Add to Cart";s:5:"image";s:0:"";s:7:"excerpt";s:140:"Add a sticky content bar with an Add to Cart button to your WooCommerce store, which is displayed as the user scrolls down the product page.";s:4:"link";s:132:"https://woocommerce.com/products/storefront-sticky-add-to-cart-2/?utm_source=product&utm_medium=upsell&utm_campaign=storefrontaddons";s:5:"price";s:9:"&#36;0.00";}i:6;O:8:"stdClass":5:{s:5:"title";s:21:"Storefront Footer Bar";s:5:"image";s:0:"";s:7:"excerpt";s:132:"Adds a full-width widget region above the Storefront footer widget area, which can be customized with colors and a background image.";s:4:"link";s:122:"https://woocommerce.com/products/storefront-footer-bar/?utm_source=product&utm_medium=upsell&utm_campaign=storefrontaddons";s:5:"price";s:9:"&#36;0.00";}i:7;O:8:"stdClass":5:{s:5:"title";s:21:"Storefront Mega Menus";s:5:"image";s:0:"";s:7:"excerpt";s:103:"Create custom,full-width dropdowns that seamlessly tie into your Storefront-powered WooCommerce shop.";s:4:"link";s:122:"https://woocommerce.com/products/storefront-mega-menus/?utm_source=product&utm_medium=upsell&utm_campaign=storefrontaddons";s:5:"price";s:10:"&#36;39.00";}i:8;O:8:"stdClass":5:{s:5:"title";s:18:"Storefront Reviews";s:5:"image";s:0:"";s:7:"excerpt";s:162:"Reviews can often be the deciding factor when making a purchase online. Highlight your best reviews on your homepage, or across your site with Storefront Reviews.";s:4:"link";s:119:"https://woocommerce.com/products/storefront-reviews/?utm_source=product&utm_medium=upsell&utm_campaign=storefrontaddons";s:5:"price";s:10:"&#36;19.00";}i:9;O:8:"stdClass":5:{s:5:"title";s:25:"Storefront Pricing Tables";s:5:"image";s:0:"";s:7:"excerpt";s:145:"List features between products, then give visitors a way to add their chosen product to their cart, with the Storefront Pricing Tables extension.";s:4:"link";s:126:"https://woocommerce.com/products/storefront-pricing-tables/?utm_source=product&utm_medium=upsell&utm_campaign=storefrontaddons";s:5:"price";s:10:"&#36;19.00";}i:10;O:8:"stdClass":5:{s:5:"title";s:23:"Storefront Product Hero";s:5:"image";s:0:"";s:7:"excerpt";s:121:"Highlight your star products in style. Add beautiful product hero components to any page at your Storefront powered shop.";s:4:"link";s:124:"https://woocommerce.com/products/storefront-product-hero/?utm_source=product&utm_medium=upsell&utm_campaign=storefrontaddons";s:5:"price";s:10:"&#36;19.00";}i:11;O:8:"stdClass":5:{s:5:"title";s:26:"Storefront Blog Customiser";s:5:"image";s:0:"";s:7:"excerpt";s:107:"This extension gives you control over the appearance of the blog on your Storefront-powered WordPress site.";s:4:"link";s:127:"https://woocommerce.com/products/storefront-blog-customiser/?utm_source=product&utm_medium=upsell&utm_campaign=storefrontaddons";s:5:"price";s:10:"&#36;19.00";}i:12;O:8:"stdClass":5:{s:5:"title";s:24:"Storefront Parallax Hero";s:5:"image";s:0:"";s:7:"excerpt";s:89:"Add an attention grabbing, immersive parallax hero component to your Storefront homepage.";s:4:"link";s:125:"https://woocommerce.com/products/storefront-parallax-hero/?utm_source=product&utm_medium=upsell&utm_campaign=storefrontaddons";s:5:"price";s:10:"&#36;19.00";}}}', 'no'),
